INSERT IGNORE INTO `draft_blog_article` (`draftArticleId`, `title`, `handle`, `autoHandleSlug`, `absURL`, `blogCategoryId`, `text`, `textHtml`, `textRawDraftContentState`, `imageId`, `createdAt`, `updatedAt`, `publishedAt`, `orderNumber`, `managerId`, `existingArticleId`, `isDraftSaved`, `unPublished`, `notSearchable`, `notInList`, `keyTextHtml`) VALUES
