INSERT IGNORE INTO `draft_blog_article` (`draftArticleId`, `title`, `handle`, `autoHandleSlug`, `absURL`, `blogCategoryId`, `text`, `textHtml`, `textRawDraftContentState`, `imageId`, `createdAt`, `updatedAt`, `publishedAt`, `orderNumber`, `managerId`, `existingArticleId`, `isDraftSaved`, `unPublished`, `notSearchable`, `notInList`, `keyTextHtml`) VALUES
	(_binary 0xab31bc88da3311edb1aa00155d4a3087, 'Давайте познакомимся! Дмитрий, компьютерный мастер.', 'dmitrij-kompyuternyj-master', 'davajte-poznakomimsya-dmitrij-kompyuternyj-master', '/', NULL, 'Я Дмитрий, компьютерный мастер. В 2013 году я закончил Российский Технологический Университет, Московский Институт Радиотехники, Электроники и Автоматики (РТУ МИРЭА). Закончил факультет Информационных технологий бюджетное отделение, специалитет. Много работал сборщиком и настройщиком компьютеров в разных местах. Моя должность на первом месте работы была именно "веб-программист", и эти навыки я тоже продолжаю использовать. В качестве компьютерного мастера инженера получалось зарабатывать чуть больше и в такой работе есть фиксированный спектр задач. А на этом сайте я сейчас пишу некое подобие своей CMS в учебный целях, свой движок сайта специально для Балашихи, наверное.', '<p>Я Дмитрий, компьютерный мастер. В 2013 году я закончил Российский Технологический Университет, Московский Институт Радиотехники, Электроники и Автоматики (РТУ МИРЭА). Закончил факультет Информационных технологий бюджетное отделение, специалитет.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n<p>Много работал сборщиком и настройщиком компьютеров в разных местах. Моя должность на первом месте работы была именно "веб-программист", и эти навыки я тоже продолжаю использовать. В качестве компьютерного мастера инженера получалось зарабатывать чуть больше и в такой работе есть фиксированный спектр задач. А на этом сайте я сейчас пишу некое подобие своей CMS в учебный целях, свой движок сайта специально для Балашихи, наверное.</p>', NULL, NULL, '2023-04-13 21:14:40', '2023-04-13 21:14:48', NULL, NULL, 1, 77, 1, NULL, NULL, NULL, '<p>dsfsdfsadf&nbsp; &nbsp;bbbsdfsd adsfdsafa sdf&nbsp; &nbsp;</p>');
INSERT IGNORE INTO `draft_blog_article` (`draftArticleId`, `title`, `handle`, `autoHandleSlug`, `absURL`, `blogCategoryId`, `text`, `textHtml`, `textRawDraftContentState`, `imageId`, `createdAt`, `updatedAt`, `publishedAt`, `orderNumber`, `managerId`, `existingArticleId`, `isDraftSaved`, `unPublished`, `notSearchable`, `notInList`, `keyTextHtml`) VALUES
	(_binary 0xaba0a642d94a11edac0300155de19352, 'Дмитрий, компьютерный мастер в Балашихе, МГТУ МИРЭА, Российский Технологический Университет', 'chastnyj-kompyuternyj-master-mgtu-mirea-(rossijskij-tehnologicheskij-universitet)', 'dmitrij-kompyuternyj-master-v-balashihe-mgtu-mirea-rossijskij-tehnologicheskij-universitet', NULL, NULL, 'В 2013 году я закончил Московский Институт Радиотехники, Электроники и Автоматики (РТУ МИРЭА), бюджетное отделение факультета Информационных технологий, и именно специалитет. Пока учился по чуть-чуть работал настройщиком компьютеров эникейщиком, что лучше назвать словами "компьютерный мастер". Хотя моя должность на первом месте работы была именно "веб-программист", и это первая запись в моей трудовой книжке, когда мне было 19 лет. Но там мало платили и я забросил такое "веб-программирование". Работая мастером эникейщиком получалось заработать даже больше за меньшее время. На этом сайте я сейчас пишу программирую свою CMS, то есть свой движок сайта больше в учебный целях. Играюсь здесь с веб-технологиями. Перед выпуском в МИРЭА должна быть производственная практика. Меня отправили поработать в предприятие в Москве. Там меня взяли на постоянную работу, поэтому я там задержался. Моя должность называлась инженер сервисного обслуживания и ремонта, и работал я в отделе сервисного обслуживания и ремонта, службы информационных технологий. Конечно здесь речь идёт о сервисным обслуживании компьютерной техники, рабочих станций, коих суммарно на предприятии было более двух тысяч. Для этой должности формально обязательно требовался диплом о высшем образовании. На собеседовании был первый вопрос об известной в узких кругах семиуровневой модели ОСИ (OSI). Эта 7-ми уровневая модель ОСИ была первой наверное больше гипотетической попыткой создания современного Интернета, и в МИРЭА эту тему на занятиях очень много мусолили. Честно говоря, считаю что эта 7-ми уровневая модель ОСИ не представляет никакого практического интереса в настоящем времени. Начальник с