INSERT IGNORE INTO `draft_blog_article` (`draftArticleId`, `title`, `handle`, `autoHandleSlug`, `absURL`, `blogCategoryId`, `text`, `textHtml`, `imageId`, `textRawDraftContentState`, `createdAt`, `updatedAt`, `publishedAt`, `orderNumber`, `managerId`, `existingArticleId`, `isDraftSaved`, `unPublished`, `notSearchable`, `notInList`) VALUES
	(_binary 0x00ec2804d3e211edb22b00155d64dcb9, 'Дмитрий, частный компьютерный мастер МГТУ МИРЭА (Российский технологический университет)', 'chastnyj-kompyuternyj-master-mgtu-mirea-(rossijskij-tehnologicheskij-universitet)', 'dmitrij-chastnyj-kompyuternyj-master-mgtu-mirea-rossijskij-tehnologicheskij-universitet', NULL, NULL, 'В 2013 году я закончил Московский Институт Радиотехники Электроники и Автоматики. Мой поток был последний со специалитетом, на следующий год уже была введена повсеместная "болонская система" с бакалавриатом и магистратурой. Институт перестал выпускать "специалистов". Это была такая евроинтеграция, как я понимаю. Это я пишу к том, что я специалист, а не бакалавр или магистр. Мой специально - это Информационный системы и технологии. Когда я заканчивал МИРЭА, у него появилась приставка МГТУ в названии. На следующий год университету придумали новое название "Российский Технологический Университет", а сокращённо это теперь "РТУ МИРЭА". Ничего не перепутайте. Я закончил бесплатное бюджетное отделение, как это полагается, а не платную форму. Пока учился по чуть-чуть работал эникейщиком, что лучше назвать словами "компьютерный мастер". Хотя моя должность на первом месте работы была именно "веб-программист", и это первая запись в моей трудовой книжке, когда мне было 19 лет. Но там мало платили и я забросил такое "веб-программирование". Работая эникейщиком получалось заработать больше за меньшее время. Кстати, сейчас я решил написать свою CMS, то есть свой движок сайта в качестве эксперимента. Играюсь здесь с веб-технологиями. Перед выпуском в МИРЭА должна быть производственная практика. Меня отправили поработать в телеканал ТВ Центр (находится по адресу Москва, ул. Большая Татарская, 33, стр. 1). На самом деле от этого можно было легко уклониться и тупо не ходить ни на какую производственную практику, так как отметку о прохождении производственной практики все равно бы поставили, даже если бы я ни разу не был в этом ТВ Центре (это мне потом рассказали). Но вот на ТВ Центре меня сразу взяли на постоянную работу, поэтому я там задержался. Моя должность называлась инженер сервисного обслуживания и ремонта, и работал я в отделе сервисного обслуживания и ремонта, сокращённо ОСОИР. В производственном отделе информационных технологий. Конечно здесь речь идёт', '<p>В 2013 году я закончил Московский Институт Радиотехники Электроники и Автоматики. Мой поток был последний со специалитетом, на следующий год уже была введена повсеместная "болонская система" с бакалавриатом и магистратурой. Институт перестал выпускать "специалистов". Это была такая евроинтеграция, как я понимаю.&nbsp;</p>\n<p>Это я пишу к том, что я специалист, а не бакалавр или магистр. Мой специально - это Информационный системы и технологии. Когда я заканчивал МИРЭА, у него появилась приставка МГТУ в названии. На следующий год университету придумали новое название "Российский Технологический Университет", а сокращённо это теперь "РТУ МИРЭА". Ничего не перепутайте. Я закончил бесплатное бюджетное отделение, как это полагается, а не платную форму.&nbsp;</p>\n<p>Пока учился по чуть-чуть работал эникейщиком, что лучше назвать словами "компьютерный мастер". Хотя моя должность на первом месте работы была именно "веб-программист", и это первая запись в моей трудовой книжке, когда мне было 19 лет. Но там мало платили и я забросил такое "веб-программирование". Работая эникейщиком получалось заработать больше за меньшее время. Кстати, сейчас я решил написать свою CMS, то есть свой движок сайта в качестве эксперимента. Играюсь здесь с веб-технологиями.</p>\n<p>Перед выпуском в МИРЭА должна быть производственная практика. Меня отправили поработать в телеканал ТВ Центр (находится по адресу Москва, ул. Большая Татарская, 33, стр. 1). На самом деле от этого можно было легко уклониться и тупо не ходить ни на какую производственную практику, так как отметку о прохождении производственной практики все равно бы поставили, даже если бы я ни разу не был в этом ТВ Центре (это мне потом рассказали). Но вот на ТВ Центре меня сразу взяли на постоянную работу, поэтому я там задержался. Моя должность называлась инженер сервисного обслуживания и ремонта, и работал я в отделе сервисного обслуживания и ремонта, сокращённо ОСОИР. В производственном отделе информационных технологий. Конечно здесь речь идёт&nbsp;</p>', NULL, NULL, '2023-04-05 19:29:24', '2023-04-05 20:12:26', NULL, NULL, 1, 76, 1, NULL, NULL, NULL);
INSERT IGNORE INTO `draft_blog_article` (`draftArticleId`, `title`, `handle`, `autoHandleSlug`, `absURL`, `blogCategoryId`, `text`, `textHtml`, `imageId`, `textRawDraftContentState`, `createdAt`, `updatedAt`, `publishedAt`, `orderNumber`, `managerId`, `existingArticleId`, `isDraftSaved`, `unPublished`, `notSearchable`, `notInList`) VALUES
	(_binary 0x0695b158d3e811edb22b00155d64dcb9, 'Дмитрий, частный компьютерный мастер МГТУ МИРЭА (Российский технологический университет)', 'chastnyj-kompyuternyj-master-mgtu-mirea-(rossijskij-tehnologicheskij-universitet)', 'dmitrij-chastnyj-kompyuternyj-master-mgtu-mirea-rossijskij-tehnologicheskij-universitet', NULL, NULL, 'В 2013 году я закончил Московский Институт Радиотехники Электроники и Автоматики. Мой поток был последний со специалитетом, на следующий год уже была введена повсеместная "болонская система" с бакалавриатом и магистратурой. Институт перестал выпускать "специалистов". Это была такая евроинтеграция, как я понимаю. Это я пишу к том, что я специалист, а не бакалавр или магистр. Мой специально - это Информационный системы и технологии. Когда я заканчивал МИРЭА, у него появилась приставка МГТУ в названии. На следующий год университету придумали новое название "Российский Технологический Университет", а сокращённо это теперь "РТУ МИРЭА". Ничего не перепутайте. Я закончил бесплатное бюджетное отделение, как это полагается, а не платную форму. Пока учился по чуть-чуть работал эникейщиком, что лучше назвать словами "компьютерный мастер". Хотя моя должность на первом месте работы была именно "веб-программист", и это первая запись в моей трудовой книжке, когда мне было 19 лет. Но там мало платили и я забросил такое "веб-программирование". Работая эникейщиком получалось заработать больше за меньшее время. Кстати, сейчас я решил написать свою CMS, то есть свой движок сайта в качестве эксперимента. Играюсь здесь с веб-технологиями. Перед выпуском в МИРЭА должна быть производственная практика. Меня отправили поработать в телеканал ТВ Центр (находится по адресу Москва, ул. Большая Татарская, 33, стр. 1). На самом деле от этого можно было легко уклониться и тупо не ходить ни на какую производственную практику, так как отметку о прохождении производственной практики все равно бы поставили, даже если бы я ни разу не был в этом ТВ Центре (это мне потом рассказали). 